class Solution {
public:
	vector<int> pathsWithMaxScore(vector<string>& board) {
		constexpr int MOD = 1000000007;
		size_t m = board.size();
		size_t n = board[0].size();
		//           pair<points, numPaths>
		vector< vector< pair<int,int> > > dp(m, vector< pair<int,int> >(n, {0,0}));
		dp.back().back().second = 1;
		
		for (int r = m-1, c = n-2; c >= 0 && board[r].at(c) != 'X'; --c) {
			dp[r][c].first = dp[r][c+1].first%MOD + (board[r].at(c)-48)%MOD;
			dp[r][c].second = 1;
		}
		
		for (int r = m-2, c = n-1; r >=0 && board[r].at(c) != 'X'; --r) {
			dp[r][c].first = dp[r+1][c].first%MOD + (board[r].at(c)-48)%MOD;
			dp[r][c].second = 1;
		}
		
		for (int r = m-2; r >= 0; --r) {
			for (int c = n-2; c >= 0; --c) {
				if (board[r].at(c) == 'X') continue;               
				
				auto [rightPoints, rightCount] = dp[r][c+1];
				auto [bottomPoints, bottomCount] = dp[r+1][c];
				auto [bottomRightPoints, bottomRightCount] = dp[r+1][c+1];
				
				if (rightCount == 0 && bottomCount == 0 && bottomRightCount == 0) {
					continue;
				}
				
				int currMaxPoints = std::max( {rightPoints, bottomPoints, bottomRightPoints} );
				dp[r][c].first = currMaxPoints + ((r==0&&c==0) ? 0 : (board[r].at(c)-48));
				
				if ( rightPoints == currMaxPoints ) dp[r][c].second += rightCount%MOD;
				if ( bottomPoints == currMaxPoints ) dp[r][c].second += bottomCount%MOD;
				if ( bottomRightPoints == currMaxPoints ) dp[r][c].second += bottomRightCount%MOD;
			}
		}
		
		return {dp[0][0].first%MOD, dp[0][0].second%MOD};
	}
};

